mapper.update:
UPDATE address
userid = #{userId}, contactperson = #{contactPerson}, addressdetail = #{addressDetail}, contactphone = #{contactPhone}, isdefault = #{isDefault},
WHERE id = #{id}

mapper.select:
SELECT a.* FROM address a

    AND a.userid = #{userId}
    AND a.contactperson LIKE concat('%', #{contactPerson}, '%')
    AND a.addressdetail LIKE concat('%', #{addressDetail}, '%')
    AND a.isdefault = #{isDefault}

ORDER BY a.isdefault DESC, a.id DESC

    LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*) FROM address a

    AND a.userid = #{userId}
    AND a.contactperson LIKE concat('%', #{contactPerson}, '%')
    AND a.addressdetail LIKE concat('%', #{addressDetail}, '%')
    AND a.isdefault = #{isDefault}
