mapper.update:
UPDATE content
name = #{name}, detail = #{detail}, cover = #{cover},
WHERE id = #{id}

mapper.select:
SELECT c.*, u.username, u.useravatar FROM content c
LEFT JOIN "user" u ON u.id = c.userid

    AND c.id = #{id}
    AND c.name LIKE concat('%', #{name}, '%')
    AND c.userid = #{userId}
    AND c.createtime BETWEEN #{startTime} AND #{endTime}

ORDER BY c.id DESC
LIMIT #{size} OFFSET (#{current}-1)*#{size}

SELECT COUNT(*) FROM content c

    AND c.id = #{id}
    AND c.name LIKE concat('%', #{name}, '%')
    AND c.userid = #{userId}
    AND c.createtime BETWEEN #{startTime} AND #{endTime}
